// src/components/Sidebar.js
import React from 'react';
import { NavLink } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Sidebar = () => {
    const { user } = useAuth(); // Obtén el usuario para verificar roles

    // Función para verificar si el usuario es administrador
    const hasAdminRole = user && user.role_name === 'Administrador'; // O el nombre exacto de tu rol de admin

    if (!user) { // Verificación de seguridad
        return null;
    }

    return (
        <aside className="bg-gray-800 text-white w-64 p-4 flex flex-col z-20">
            <div className="text-2xl font-bold mb-8">ITAM System</div>
            <nav className="flex-1">
                <ul>
                    <li className="mb-2">
                        <NavLink
                            to="/home"
                            className={({ isActive }) =>
                                `block py-2 px-4 rounded transition duration-200 ${isActive ? 'bg-gray-700' : 'hover:bg-gray-700'}`
                            }
                        >
                            Inicio
                        </NavLink>
                    </li>
                    {hasAdminRole && ( // Renderiza esto solo si es administrador
                        <li className="mb-2">
                            <NavLink
                                to="/users"
                                className={({ isActive }) =>
                                    `block py-2 px-4 rounded transition duration-200 ${isActive ? 'bg-gray-700' : 'hover:bg-gray-700'}`
                                }
                            >
                                Gestión de Usuarios
                            </NavLink>
                        </li>
                    )}
                    {/* Agrega más enlaces de navegación aquí */}
                </ul>
            </nav>
        </aside>
    );
};

export default Sidebar;
