import React, { useState, useEffect } from 'react';
import api from '../api'; 
import { toast } from 'react-toastify';
import { useAuth } from '../context/AuthContext';
// ----------------------------------------------------------------
// ðŸ†• IMPORTA EL NUEVO COMPONENTE DE AUTOCOMPLETADO
import MasterDataAutocomplete from './MasterDataAutocomplete'; 
// ----------------------------------------------------------------

// Opciones para los campos de selecciÃ³n estÃ¡ticos
const PUESTO_CHOICES = [
    'Gerente', 'Coordinador', 'Analista', 'TÃ©cnico', 'Desarrollador', 'Soporte', 'Otro'
];
const STATUS_CHOICES = [
    'Activo', 'Inactivo', 'Vacaciones', 'Licencia'
];

function UserForm({ user, onClose, onSubmit, roles }) {
    const isEditing = !!user;
    const { user: loggedInUser } = useAuth(); // Para verificar si es superusuario
    
    const initialFormData = {
        username: '',
        email: '',
        password: '',
        first_name: '',
        last_name: '',
        puesto: '',
        departamento: null, // Debe ser null para el ID (clave forÃ¡nea)
        region: null,       // Debe ser null para el ID (clave forÃ¡nea)
        status: 'Activo',
        assigned_role_id: null, // ID del grupo/rol
        is_staff: false,
        is_superuser: false,
    };

    const [formData, setFormData] = useState(initialFormData);
    const [loading, setLoading] = useState(false);

    // Cargar datos del usuario si estamos editando
    useEffect(() => {
        if (isEditing && user) {
            // Mapeamos los datos del usuario a formData. 
            // department/region vienen como objetos en el payload, pero solo enviamos el ID.
            setFormData({
                username: user.username || '',
                email: user.email || '',
                // No se carga la contraseÃ±a
                first_name: user.first_name || '',
                last_name: user.last_name || '',
                puesto: user.puesto || '',
                // ðŸ”‘ Usamos el ID de la clave forÃ¡nea
                departamento: user.departamento || null, 
                region: user.region || null,
                status: user.status || 'Activo',
                // AsegÃºrate de obtener el ID del rol correctamente (viene como una lista, tomamos el primero)
                assigned_role_id: user.role_id || null, 
                is_staff: user.is_staff || false,
                is_superuser: user.is_superuser || false,
            });
        } else {
            setFormData(initialFormData);
        }
    }, [isEditing, user]);

    // Maneja el cambio para todos los inputs y el Autocomplete
    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        const dataToSend = {
            ...formData,
            // ðŸ”‘ El backend espera assigned_role_id como un array de IDs, si es que tiene un rol.
            assigned_role_id: formData.assigned_role_id ? [formData.assigned_role_id] : [],
        };
        
        // La contraseÃ±a solo se envÃ­a al crear
        if (isEditing) {
            delete dataToSend.password;
        }

        try {
            await onSubmit(dataToSend); // Llama a la funciÃ³n del padre (Create/Update)
            onClose();
        } catch (error) {
            // Los errores ya se manejan en UserCrud con toast, pero por si acaso.
            console.error(isEditing ? 'Error al actualizar:' : 'Error al crear:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {/* Usuario y Email */}
                <div>
                    <label htmlFor="username" className="block text-sm font-medium text-gray-700">Usuario <span className="text-red-500">*</span></label>
                    <input
                        type="text"
                        name="username"
                        id="username"
                        value={formData.username}
                        onChange={handleChange}
                        required
                        disabled={isEditing} // No permitir cambiar el username al editar
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                    />
                </div>
                <div>
                    <label htmlFor="email" className="block text-sm font-medium text-gray-700">Email <span className="text-red-500">*</span></label>
                    <input
                        type="email"
                        name="email"
                        id="email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                    />
                </div>
                
                {/* ContraseÃ±a (Solo en creaciÃ³n) */}
                {!isEditing && (
                    <div>
                        <label htmlFor="password" className="block text-sm font-medium text-gray-700">ContraseÃ±a <span className="text-red-500">*</span></label>
                        <input
                            type="password"
                            name="password"
                            id="password"
                            value={formData.password}
                            onChange={handleChange}
                            required={!isEditing}
                            className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                        />
                    </div>
                )}
                
                {/* Nombre y Apellido */}
                <div>
                    <label htmlFor="first_name" className="block text-sm font-medium text-gray-700">Nombre</label>
                    <input
                        type="text"
                        name="first_name"
                        id="first_name"
                        value={formData.first_name}
                        onChange={handleChange}
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                    />
                </div>
                <div>
                    <label htmlFor="last_name" className="block text-sm font-medium text-gray-700">Apellido</label>
                    <input
                        type="text"
                        name="last_name"
                        id="last_name"
                        value={formData.last_name}
                        onChange={handleChange}
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                    />
                </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {/* Puesto */}
                <div>
                    <label htmlFor="puesto" className="block text-sm font-medium text-gray-700">Puesto</label>
                    <select
                        name="puesto"
                        id="puesto"
                        value={formData.puesto}
                        onChange={handleChange}
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                    >
                        <option value="">Seleccione Puesto</option>
                        {PUESTO_CHOICES.map(choice => (
                            <option key={choice} value={choice}>{choice}</option>
                        ))}
                    </select>
                </div>

                {/* ---------------------------------------------------------------- */}
                {/* ðŸ”‘ NUEVO: MasterDataAutocomplete para Departamento */}
                <MasterDataAutocomplete
                    label="Departamento"
                    name="departamento"
                    endpoint="masterdata/departamentos/" // Endpoint de tu API
                    value={formData.departamento} 
                    onChange={handleChange}       
                />
                {/* ---------------------------------------------------------------- */}
                
                {/* ---------------------------------------------------------------- */}
                {/* ðŸ”‘ NUEVO: MasterDataAutocomplete para RegiÃ³n */}
                <MasterDataAutocomplete
                    label="RegiÃ³n"
                    name="region"
                    endpoint="masterdata/regions/" 
                    value={formData.region}
                    onChange={handleChange}
                />
                {/* ---------------------------------------------------------------- */}

            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {/* Rol */}
                <div>
                    <label htmlFor="assigned_role_id" className="block text-sm font-medium text-gray-700">Rol</label>
                    <select
                        name="assigned_role_id"
                        id="assigned_role_id"
                        value={formData.assigned_role_id || ''}
                        onChange={handleChange}
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                    >
                        <option value="">Seleccione Rol</option>
                        {roles && roles.map(role => (
                            <option key={role.id} value={role.id}>{role.name}</option>
                        ))}
                    </select>
                </div>
                
                {/* Estado */}
                {isEditing && (
                    <div>
                        <label htmlFor="status" className="block text-sm font-medium text-gray-700">Estado</label>
                        <select
                            name="status"
                            id="status"
                            value={formData.status}
                            onChange={handleChange}
                            className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                        >
                            {STATUS_CHOICES.map(choice => (
                                <option key={choice} value={choice}>{choice}</option>
                            ))}
                        </select>
                    </div>
                )}
                
                {/* is_staff / is_superuser (Solo visible y editable por Superuser) */}
                {loggedInUser && loggedInUser.is_superuser && (
                    <div className="col-span-1 md:col-span-1 flex items-end space-x-4 pb-1">
                        <div className="flex items-center">
                            <input
                                type="checkbox"
                                name="is_staff"
                                id="is_staff"
                                checked={formData.is_staff}
                                onChange={handleChange}
                                className="h-4 w-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                            />
                            <label htmlFor="is_staff" className="ml-2 block text-sm font-medium text-gray-700">Es Staff</label>
                        </div>
                        <div className="flex items-center">
                            <input
                                type="checkbox"
                                name="is_superuser"
                                id="is_superuser"
                                checked={formData.is_superuser}
                                onChange={handleChange}
                                className="h-4 w-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                            />
                            <label htmlFor="is_superuser" className="ml-2 block text-sm font-medium text-gray-700">Es Superuser</label>
                        </div>
                    </div>
                )}
            </div>
            
            <div className="flex justify-end space-x-3 pt-6 border-t border-gray-200">
                <button
                    type="button"
                    onClick={onClose}
                    className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                >
                    Cancelar
                </button>
                <button
                    type="submit"
                    disabled={loading}
                    className="px-4 py-2 bg-blue-600 text-white rounded-md text-sm font-medium hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
                >
                    {loading ? 'Guardando...' : (isEditing ? 'Actualizar Usuario' : 'Crear Usuario')}
                </button>
            </div>
        </form>
    );
}

export default UserForm;
