// C:\\Proyectos\\ITAM_System\\itam_frontend\\src\\components\\ModeloComputoForm.js

import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import { createModeloComputo, updateModeloComputo } from '../api';

function ModeloComputoForm({ modelo, onClose, onSave, marcas, tiposActivo }) {
    const isEditing = !!modelo;

    const [formData, setFormData] = useState({
        name: '',
        marca: '', // ID de la Marca
        tipo_activo: '', // ID del Tipo de Activo
        specifications: '',
    });
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        if (isEditing && modelo) {
            setFormData({
                name: modelo.name || '',
                // Para edición, usamos el ID del objeto (que es lo que el serializador espera para 'marca')
                marca: modelo.marca || '',
                tipo_activo: modelo.tipo_activo || '',
                specifications: modelo.specifications || '',
            });
        }
    }, [isEditing, modelo]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        // Convertir a null si el valor es un string vacío para FKs opcionales
        const finalValue = (name === 'tipo_activo' && value === '') ? null : value;
        setFormData(prev => ({ ...prev, [name]: finalValue }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        if (!formData.name || !formData.marca) {
            toast.error('El nombre y la marca son obligatorios.');
            setLoading(false);
            return;
        }

        try {
            const dataToSend = {
                ...formData,
                marca: Number(formData.marca),
                // Si el campo es null, se envía null. Si tiene valor, se convierte a número.
                tipo_activo: formData.tipo_activo ? Number(formData.tipo_activo) : null,
            };

            if (isEditing) {
                await updateModeloComputo(modelo.id, dataToSend);
                toast.success('Modelo de Cómputo actualizado con éxito.');
            } else {
                await createModeloComputo(dataToSend);
                toast.success('Modelo de Cómputo creado con éxito.');
            }
            onSave();
            onClose();
        } catch (error) {
            console.error('Error al guardar el modelo:', error.response?.data || error.message);
            const errorMsg = error.response?.data?.name?.[0] || 'Error al guardar el modelo. Ya existe un modelo con ese nombre o hay un error de datos.';
            toast.error(errorMsg);
        } finally {
            setLoading(false);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-4">
            <div>
                <label htmlFor="name" className="block text-sm font-medium text-gray-700">Nombre del Modelo *</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                />
            </div>

            <div>
                <label htmlFor="marca" className="block text-sm font-medium text-gray-700">Marca *</label>
                <select
                    id="marca"
                    name="marca"
                    value={formData.marca}
                    onChange={handleChange}
                    required
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                >
                    <option value="">Seleccione una marca</option>
                    {marcas.map(marca => (
                        <option key={marca.id} value={marca.id}>{marca.name}</option>
                    ))}
                </select>
            </div>

            <div>
                <label htmlFor="tipo_activo" className="block text-sm font-medium text-gray-700">Tipo de Activo (Opcional)</label>
                <select
                    id="tipo_activo"
                    name="tipo_activo"
                    value={formData.tipo_activo || ''}
                    onChange={handleChange}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                >
                    <option value="">Ninguno</option>
                    {tiposActivo.map(tipo => (
                        <option key={tipo.id} value={tipo.id}>{tipo.name}</option>
                    ))}
                </select>
            </div>

            <div>
                <label htmlFor="specifications" className="block text-sm font-medium text-gray-700">Especificaciones Técnicas</label>
                <textarea
                    id="specifications"
                    name="specifications"
                    rows="3"
                    value={formData.specifications}
                    onChange={handleChange}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                />
            </div>

            <div className="flex justify-end space-x-3 pt-4">
                <button
                    type="button"
                    onClick={onClose}
                    className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50"
                >
                    Cancelar
                </button>
                <button
                    type="submit"
                    disabled={loading}
                    className="px-4 py-2 bg-blue-600 text-white rounded-md text-sm font-medium hover:bg-blue-700 disabled:opacity-50"
                >
                    {loading ? 'Guardando...' : (isEditing ? 'Actualizar Modelo' : 'Crear Modelo')}
                </button>
            </div>
        </form>
    );
}

export default ModeloComputoForm;
