import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify'; // Dependencia asumida disponible

// =========================================================================
// MOCK COMPONENTS & API FUNCTIONS (PARA AUTO-CONTENIDO Y EJECUTABLE)
// NOTA: En tu proyecto real, debes descomentar tus imports originales.
// =========================================================================

// MOCK: Componente Modal
const Modal = ({ show, onClose, title, children }) => {
    if (!show) return null;

    return (
        <div className="fixed inset-0 z-50 overflow-y-auto bg-gray-600 bg-opacity-75 transition-opacity" aria-labelledby="modal-title" role="dialog" aria-modal="true">
            <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
                <div className="fixed inset-0 transition-opacity" aria-hidden="true"></div>
                <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
                
                <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-4xl sm:w-full">
                    <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                        <div className="sm:flex sm:items-start">
                            <div className="mt-3 text-center sm:mt-0 sm:text-left w-full">
                                <h3 className="text-xl leading-6 font-extrabold text-gray-900 border-b pb-2 mb-4" id="modal-title">
                                    {title}
                                </h3>
                                <div>
                                    {children}
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                        <button type="button" onClick={onClose} className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-100 focus:outline-none sm:ml-3 sm:w-auto sm:text-sm">
                            Cerrar
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

// MOCK: Funciones de API y Datos
const mockMarcas = [
    { id: 'hp', name: 'HP' }, 
    { id: 'dell', name: 'Dell' },
    { id: 'lenovo', name: 'Lenovo' }
];
const mockTiposActivos = [
    { id: 'laptop', name: 'Laptop' }, 
    { id: 'desktop', name: 'Desktop' },
    { id: 'tablet', name: 'Tablet' }
];

// Simulación de llamadas a la API
const createModeloComputo = async (data) => new Promise(resolve => setTimeout(() => resolve({ message: 'Created' }), 500));
const updateModeloComputo = async (id, data) => new Promise(resolve => setTimeout(() => resolve({ message: 'Updated' }), 500));
const getMarcas = async () => new Promise(resolve => setTimeout(() => resolve({ data: mockMarcas }), 300));
const getTiposActivos = async () => new Promise(resolve => setTimeout(() => resolve({ data: mockTiposActivos }), 300));

// =========================================================================
// FIN DE MOCK
// =========================================================================


const initialFormData = {
    marca: '',
    tipo_activo: '',
    codigo_interno: '', // Renombrado a "Nombre Modelo" en UI
    procesador: '',
    ram: '',
    almacenamiento: '',
    tarjeta_grafica: '',
    interfaz_wifi: true,
    mac_wifi: '', // Nuevo: MAC Address para Wi-Fi
    interfaz_ethernet: true,
    mac_ethernet: '', // Nuevo: MAC Address para Ethernet
    interfaz_serial_paralelo: false,
    serie_cargador: '',
    cuenta_con_docking: false,
    serie_docking: '',
    cuenta_con_pantalla: false,
    serie_pantalla: '',
    cuenta_con_teclado: false,
    serie_teclado: '',
    cuenta_con_mouse: false,
    serie_mouse: '',
    cuenta_con_mochila: false,
};

const ModeloComputoFormModal = ({ show, onClose, onSaveSuccess, modeloToEdit }) => {
    const [formData, setFormData] = useState(initialFormData);
    const [marcas, setMarcas] = useState([]);
    const [tiposActivos, setTiposActivos] = useState([]);
    const [loading, setLoading] = useState(false);
    const [errors, setErrors] = useState({});

    useEffect(() => {
        // Cargar datos para selects
        const loadDependencies = async () => {
            try {
                const [marcasRes, tiposRes] = await Promise.all([getMarcas(), getTiposActivos()]);
                setMarcas(marcasRes.data);
                setTiposActivos(tiposRes.data);
            } catch (error) {
                console.error('Error cargando dependencias:', error);
                toast.error('Error al cargar marcas o tipos de activos.');
            }
        };
        loadDependencies();

        // Cargar datos para edición
        if (modeloToEdit) {
            setFormData(prev => ({
                ...initialFormData,
                ...modeloToEdit,
                // Asegurar valores por defecto para los nuevos campos si no vienen de la API
                mac_wifi: modeloToEdit.mac_wifi || '',
                mac_ethernet: modeloToEdit.mac_ethernet || '',
                // Asegurar valores para los campos originales
                marca: modeloToEdit.marca || '',
                tipo_activo: modeloToEdit.tipo_activo || '',
                serie_docking: modeloToEdit.serie_docking || '',
                serie_pantalla: modeloToEdit.serie_pantalla || '',
                serie_teclado: modeloToEdit.serie_teclado || '',
                serie_mouse: modeloToEdit.serie_mouse || '',
                tarjeta_grafica: modeloToEdit.tarjeta_grafica || '',
                serie_cargador: modeloToEdit.serie_cargador || '',
            }));
        } else {
            setFormData(initialFormData);
        }
    }, [modeloToEdit]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        const newValue = type === 'checkbox' ? checked : value;

        setFormData(prev => ({ ...prev, [name]: newValue }));
        setErrors(prev => ({ ...prev, [name]: null }));
    };

    // Función para manejar el cambio de checkbox de Interfaces (Wi-Fi/Ethernet)
    const handleInterfaceChange = (e, macFieldName) => {
        const { name, checked } = e.target;
        setFormData(prev => ({ 
            ...prev, 
            [name]: checked,
            // Limpiar y deshabilitar el campo MAC si se desmarca la interfaz
            [macFieldName]: checked ? prev[macFieldName] : ''
        }));
        // Limpiar error asociado a la MAC si se desactiva
        if (!checked) {
            setErrors(prev => ({ ...prev, [macFieldName]: null }));
        }
    };

    // Función para manejar el cambio de checkbox de accesorios y limpiar el campo de serie asociado si se desmarca
    const handleAccessoryChange = (e, seriesFieldName) => {
        const { name, checked } = e.target;
        setFormData(prev => ({ 
            ...prev, 
            [name]: checked,
            // Limpiar y deshabilitar el campo de serie si se desmarca el accesorio
            [seriesFieldName]: checked ? prev[seriesFieldName] : ''
        }));
        // Limpiar error asociado a la serie si se desactiva
        if (!checked) {
            setErrors(prev => ({ ...prev, [seriesFieldName]: null }));
        }
    };

    const validateConditionalFields = (data, newErrors) => {
        // Validaciones condicionales de MAC Address
        if (data.interfaz_wifi && !data.mac_wifi) {
            newErrors.mac_wifi = 'La MAC Wi-Fi es obligatoria si la interfaz está marcada.';
        }
        if (data.interfaz_ethernet && !data.mac_ethernet) {
            newErrors.mac_ethernet = 'La MAC Ethernet es obligatoria si la interfaz está marcada.';
        }
        
        // Validaciones condicionales de Accesorios
        if (data.cuenta_con_docking && !data.serie_docking) {
            newErrors.serie_docking = 'La serie de docking es obligatoria si cuenta con docking.';
        }
        if (data.cuenta_con_pantalla && !data.serie_pantalla) {
            newErrors.serie_pantalla = 'La serie de pantalla es obligatoria si cuenta con pantalla.';
        }
        if (data.cuenta_con_teclado && !data.serie_teclado) {
            newErrors.serie_teclado = 'La serie de teclado es obligatoria si cuenta con teclado.';
        }
        if (data.cuenta_con_mouse && !data.serie_mouse) {
            newErrors.serie_mouse = 'La serie de mouse es obligatoria si cuenta con mouse.';
        }
    };

    const validate = (data) => {
        const newErrors = {};
        // Validaciones Obligatorias
        if (!data.marca) newErrors.marca = 'La marca es obligatoria.';
        if (!data.tipo_activo) newErrors.tipo_activo = 'El tipo de activo es obligatorio.';
        if (!data.codigo_interno) newErrors.codigo_interno = 'El Nombre del Modelo es obligatorio.'; 
        if (!data.procesador) newErrors.procesador = 'El procesador es obligatorio.';
        if (!data.ram) newErrors.ram = 'La RAM es obligatoria.';
        if (!data.almacenamiento) newErrors.almacenamiento = 'El almacenamiento es obligatorio.';

        validateConditionalFields(data, newErrors);
        
        return newErrors;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const findErrors = validate(formData);
        
        if (Object.keys(findErrors).length > 0) {
            setErrors(findErrors);
            toast.error('Por favor, corrige los errores del formulario.');
            return;
        }

        setLoading(true);
        try {
            // Se elimina el campo 'serie' antes de enviar (por si existía en el objeto de edición)
            const payload = { ...formData };
            delete payload.serie; 
            
            if (modeloToEdit) {
                await updateModeloComputo(modeloToEdit.id, payload);
                toast.success('Modelo de cómputo actualizado exitosamente!');
            } else {
                await createModeloComputo(payload);
                toast.success('Modelo de cómputo creado exitosamente!');
            }
            onSaveSuccess();
            onClose();
        } catch (error) {
            console.error('Error saving modelo:', error.response?.data || error.message);
            const apiErrors = error.response?.data;
            if (apiErrors) {
                setErrors(prev => ({ ...prev, ...apiErrors }));
            }
            const errorMsg = apiErrors?.detail || 'Error al guardar el modelo de cómputo.';
            toast.error(errorMsg);
        } finally {
            setLoading(false);
        }
    };

    // Clase de estilo para mejorar la visibilidad de los inputs de texto/select
    const getInputClasses = (name, isSeriesField, isDisabled, isSelect) => {
        let classes = `mt-1 block w-full rounded-lg border-2 p-2 shadow-sm transition duration-150 ease-in-out `;
        
        // Estilo de borde mejorado y focus (más visible)
        classes += `border-gray-400 focus:border-indigo-600 focus:ring-2 focus:ring-indigo-300 `;

        if (isDisabled) {
            classes += `bg-gray-100 cursor-not-allowed text-gray-500 `;
        } else {
            classes += `bg-white text-gray-800 `;
        }
        
        if (errors[name]) {
            classes += `!border-red-500 `;
        }

        if (isSelect) {
            classes += `appearance-none pr-8 `;
        }

        return classes.trim();
    };
    
    // Función centralizada para renderizar los campos
    const renderInput = (label, name, type = 'text', required = true, isConditional = false, seriesOrMacFieldName = null) => {
        const isCheckbox = type === 'checkbox';
        const isSelect = type === 'select';
        
        const isSeriesField = name.startsWith('serie_');
        let isDisabled = false;

        // Lógica de deshabilitación para campos condicionales (Serie y MAC)
        if (isSeriesField) {
            const controlField = name.replace('serie_', 'cuenta_con_');
            isDisabled = !formData[controlField];
        } else if (name === 'mac_wifi') {
            isDisabled = !formData.interfaz_wifi;
        } else if (name === 'mac_ethernet') {
            isDisabled = !formData.interfaz_ethernet;
        }
        
        // Define el manejador de cambio
        const handleSpecificChange = (e) => {
            if (name === 'interfaz_wifi') {
                handleInterfaceChange(e, 'mac_wifi');
            } else if (name === 'interfaz_ethernet') {
                handleInterfaceChange(e, 'mac_ethernet');
            } else if (isConditional) {
                // Para accesorios (docking, mouse, etc.)
                handleAccessoryChange(e, seriesOrMacFieldName);
            } else {
                handleChange(e);
            }
        };

        // El campo MAC es requerido si su interfaz está activa
        const isMacRequired = (name === 'mac_wifi' && formData.interfaz_wifi) || (name === 'mac_ethernet' && formData.interfaz_ethernet);
        const finalRequired = required || isMacRequired;

        return (
            <div key={name} className="relative">
                <label className="block text-sm font-semibold text-gray-700">
                    {label} {finalRequired && <span className="text-red-500">*</span>}
                </label>
                
                {isSelect ? (
                    <select
                        name={name}
                        value={formData[name] || ''}
                        onChange={handleChange}
                        className={getInputClasses(name, false, false, true)}
                    >
                        <option value="">Seleccione...</option>
                        {(name === 'marca' ? marcas : tiposActivos).map(item => (
                            <option key={item.id} value={item.id}>{item.name}</option>
                        ))}
                    </select>
                ) : isCheckbox ? (
                    <div className="flex items-center h-full pt-1">
                        <input
                            type="checkbox"
                            name={name}
                            checked={formData[name]}
                            onChange={handleSpecificChange}
                            className="h-5 w-5 text-indigo-600 border-gray-400 rounded focus:ring-indigo-500"
                        />
                    </div>
                ) : (
                    <input
                        type={type}
                        name={name}
                        value={formData[name] || ''}
                        onChange={handleChange}
                        className={getInputClasses(name, isSeriesField, isDisabled, false)}
                        disabled={isDisabled}
                        placeholder={isDisabled ? 'N/A' : (name.startsWith('mac_') ? 'Ej: AA:BB:CC:DD:EE:FF' : '')}
                    />
                )}
                
                {errors[name] && <p className="mt-1 text-xs text-red-600 font-medium">{errors[name]}</p>}
            </div>
        );
    };


    return (
        <Modal 
            show={show} 
            onClose={onClose} 
            title={modeloToEdit ? "Editar Modelo de Cómputo" : "Crear Modelo de Cómputo"}
        >
            <form onSubmit={handleSubmit} className="space-y-6">
                {/* GRID DE 2 COLUMNAS APLICADO EN MD+ */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    
                    {/* BLOQUE DE IDENTIFICACIÓN Y RELACIONES */}
                    {renderInput('Nombre Modelo', 'codigo_interno')}
                    {renderInput('Marca', 'marca', 'select')}
                    {renderInput('Tipo de Activo', 'tipo_activo', 'select')}
                    {renderInput('Tarjeta Gráfica', 'tarjeta_grafica', 'text', false)} 

                    {/* SECCIÓN DE ESPECIFICACIONES DE HARDWARE (Span 2 columnas) */}
                    <div className="col-span-1 md:col-span-2 border-t pt-4">
                        <h3 className="text-lg font-bold text-gray-800 mb-2">Especificaciones de Hardware</h3>
                    </div>
                    
                    {renderInput('Procesador', 'procesador')}
                    {renderInput('RAM (Ej: 16GB)', 'ram')} 
                    {renderInput('Almacenamiento (Ej: 512GB SSD)', 'almacenamiento')}
                    <div className="h-0 invisible"></div>


                    {/* SECCIÓN DE INTERFACES Y MAC ADDRESS (Span 2 columnas) */}
                    <div className="col-span-1 md:col-span-2 border-t pt-4">
                        <h3 className="text-lg font-bold text-gray-800 mb-2">Interfaces de Red y Dirección MAC</h3>
                    </div>
                    
                    {/* MAC Wi-Fi (Checkbox y campo de texto condicional) */}
                    <div className="p-3 border rounded-xl shadow-inner bg-indigo-50/70">
                        {renderInput('Incluye Interfaz WiFi', 'interfaz_wifi', 'checkbox', false)}
                        {renderInput('MAC Address Wi-Fi', 'mac_wifi', 'text', false)} 
                    </div>

                    {/* MAC Ethernet (Checkbox y campo de texto condicional) */}
                    <div className="p-3 border rounded-xl shadow-inner bg-indigo-50/70">
                        {renderInput('Incluye Interfaz Ethernet', 'interfaz_ethernet', 'checkbox', false)}
                        {renderInput('MAC Address Ethernet', 'mac_ethernet', 'text', false)}
                    </div>
                    
                    {/* Otras interfaces */}
                    <div className="col-span-1 md:col-span-2 pt-2">
                        {renderInput('Interfaz Serial/Paralelo', 'interfaz_serial_paralelo', 'checkbox', false)}
                    </div>
                    
                    {/* SECCIÓN DE ACCESORIOS (Span 2 columnas) */}
                    <div className="col-span-1 md:col-span-2 border-t pt-4">
                        <h3 className="text-lg font-bold text-gray-800 mb-2">Accesorios y Series</h3>
                    </div>

                    {/* Layout interno para accesorios en 2 columnas */}
                    <div className="col-span-1 md:col-span-2 grid grid-cols-1 md:grid-cols-2 gap-6">
                        
                        {/* Cargador y Mochila son campos simples */}
                        {renderInput('Serie de Cargador', 'serie_cargador', 'text', false)}
                        <div className='flex items-end'>{renderInput('Cuenta con Mochila', 'cuenta_con_mochila', 'checkbox', false)}</div>

                        {/* Accesorios Condicionales */}
                        <div className="p-3 border rounded-xl shadow-inner bg-yellow-50/50">
                            {renderInput('Cuenta con Docking', 'cuenta_con_docking', 'checkbox', false, true, 'serie_docking')}
                            {renderInput('Serie de Docking', 'serie_docking', 'text', false, false)}
                        </div>
                        <div className="p-3 border rounded-xl shadow-inner bg-yellow-50/50">
                            {renderInput('Cuenta con Pantalla', 'cuenta_con_pantalla', 'checkbox', false, true, 'serie_pantalla')}
                            {renderInput('Serie de Pantalla', 'serie_pantalla', 'text', false, false)}
                        </div>

                        <div className="p-3 border rounded-xl shadow-inner bg-yellow-50/50">
                            {renderInput('Cuenta con Teclado', 'cuenta_con_teclado', 'checkbox', false, true, 'serie_teclado')}
                            {renderInput('Serie de Teclado', 'serie_teclado', 'text', false, false)}
                        </div>
                        <div className="p-3 border rounded-xl shadow-inner bg-yellow-50/50">
                            {renderInput('Cuenta con Mouse', 'cuenta_con_mouse', 'checkbox', false, true, 'serie_mouse')}
                            {renderInput('Serie de Mouse', 'serie_mouse', 'text', false, false)}
                        </div>
                    </div>
                </div>
                
                <div className="flex justify-end space-x-3 border-t pt-6 mt-8">
                    <button
                        type="button"
                        onClick={onClose}
                        className="px-6 py-2 text-sm font-semibold text-gray-700 bg-white border border-gray-300 rounded-xl shadow-md hover:bg-gray-100 transition duration-150"
                        disabled={loading}
                    >
                        Cancelar
                    </button>
                    <button
                        type="submit"
                        className="px-6 py-2 text-sm font-semibold text-white bg-indigo-600 rounded-xl shadow-md hover:bg-indigo-700 focus:outline-none focus:ring-4 focus:ring-indigo-300 transition duration-150"
                        disabled={loading}
                    >
                        {loading ? 'Guardando...' : 'Guardar Modelo'}
                    </button>
                </div>
            </form>
        </Modal>
    );
};

export default ModeloComputoFormModal;
