    'Gerente', 'Coordinador', 'Analista', 'Técnico', 'Desarrollador', 'Soporte', 'Otro'
];
// ELIMINADO: Ya no necesitamos DEPARTAMENTO_CHOICES estático
// const DEPARTAMENTO_CHOICES = [
//     'TI', 'Recursos Humanos', 'Finanzas', 'Marketing', 'Ventas', 'Operaciones', 'Otro'
// ];
const STATUS_CHOICES = [
    'Activo', 'Inactivo', 'Vacaciones', 'Licencia'
];

function UserForm({ user, onClose }) {
    const isEditing = !!user;

    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        first_name: '',
        last_name: '',
        puesto: '',
        departamento: '', // Este será el ID del departamento
        region: '',       // Este será el ID de la región
        status: 'Activo',
        assigned_role_id: '',
        is_staff: false,
        is_superuser: false,
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [roles, setRoles] = useState([]);
    const [regions, setRegions] = useState([]);
    // NUEVO ESTADO PARA LOS DEPARTAMENTOS DINÁMICOS
    const [departamentos, setDepartamentos] = useState([]);
    const navigate = useNavigate();

    // Precargar datos si estamos editando
    useEffect(() => {
        if (isEditing && user) {
            setFormData({
                username: user.username || '',
                email: user.email || '',
                password: '',
                first_name: user.first_name || '',
                last_name: user.last_name || '',
                puesto: user.puesto || '',
                // CAMBIADO: departamento ahora es el ID del usuario.departamento
                departamento: user.departamento || '', // user.departamento ahora es el ID del departamento
                region: user.region || '',             // user.region ahora es el ID de la región
                status: user.status || 'Activo',
                assigned_role_id: user.role_id || '',
                is_staff: user.is_staff || false,
                is_superuser: user.is_superuser || false,
            });
        }
    }, [isEditing, user]);

    // useEffect para cargar los roles (grupos) desde el backend
    useEffect(() => {
        const fetchRoles = async () => {
            try {
                const response = await axios.get('/roles/'); // Asumiendo que esta es la ruta correcta para tus roles
                setRoles(response.data);
            } catch (err) {
                console.error("Error al cargar los roles:", err.response || err);
                toast.error('Error al cargar la lista de roles.');
            }
        };
        fetchRoles();
    }, []);

    // useEffect para cargar las regiones desde el backend
    useEffect(() => {
        const fetchRegionsData = async () => {
            try {
                const response = await axios.get('masterdata/regions/'); // Ruta para obtener regiones
                setRegions(response.data);
            } catch (err) {
                console.error("Error al cargar las regiones:", err.response || err);
                toast.error('Error al cargar la lista de regiones.');
            }
        };
        fetchRegionsData();
    }, []);

    // NUEVO useEffect para cargar los departamentos desde el backend
    useEffect(() => {
        const fetchDepartamentosData = async () => {
            try {
                const response = await axios.get('masterdata/departamentos/'); // Ruta para obtener departamentos
                setDepartamentos(response.data);
            } catch (err) {
                console.error("Error al cargar los departamentos:", err.response || err);
                toast.error('Error al cargar la lista de departamentos.');
            }
        };
        fetchDepartamentosData();
    }, []); // Se ejecuta solo una vez al montar

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            const dataToSend = { ...formData };

            if (isEditing && !dataToSend.password) {
                delete dataToSend.password;
            }
            if (!isEditing && !dataToSend.password) {
                setError('La contraseña es obligatoria para nuevos usuarios.');
                toast.error('La contraseña es obligatoria para nuevos usuarios.');
                setLoading(false);
                return;
            }

            if (dataToSend.assigned_role_id) {
                dataToSend.assigned_role_id = [parseInt(dataToSend.assigned_role_id, 10)];
            } else {
                dataToSend.assigned_role_id = [];
            }
            
            // IMPORTANTE: Convierte la región a null si está vacía
            if (dataToSend.region === '') {
                dataToSend.region = null;
            }
            // NUEVO: Convierte el departamento a null si está vacío
            if (dataToSend.departamento === '') {
                dataToSend.departamento = null;
            }

            if (isEditing) {
                await axios.put(`/users/${user.id}/`, dataToSend);
                toast.success('Usuario actualizado exitosamente!');
            } else {
                await axios.post('/users/', dataToSend);
                toast.success('Usuario creado exitosamente!');
            }
            onClose();
        } catch (err) {
            if (err.response && err.response.status === 401) {
                toast.error('Sesión expirada o no autorizada. Por favor, inicia sesión de nuevo.');
                localStorage.clear();
                navigate('/login');
            } else {
                const errorMsg = err.response?.data
                    ? Object.values(err.response.data).flat().join(' ')
                    : err.message;
                setError('Error: ' + errorMsg);
                console.error("Error submitting user form:", err.response || err);
                toast.error('Error al guardar usuario: ' + errorMsg);
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-4">
            {error && (
                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
                    <span className="block sm:inline">{error}</span>
                </div>
            )}

            <div>
                <label htmlFor="username" className="block text-sm font-medium text-gray-700">Usuario <span className="text-red-500">*</span></label>
                <input
                    type="text"
                    name="username"
                    id="username"
                    value={formData.username}
                    onChange={handleChange}
                    required
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                    disabled={isEditing}
                />
            </div>

            <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-700">Correo <span className="text-red-500">*</span></label>
                <input
                    type="email"
                    name="email"
                    id="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                />
            </div>

            {!isEditing && (
                <div>
                    <label htmlFor="password" className="block text-sm font-medium text-gray-700">Contraseña <span className="text-red-500">*</span></label>
                    <input
                        type="password"
                        name="password"
                        id="password"
                        value={formData.password}
                        onChange={handleChange}
                        required={!isEditing}
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                    />
                </div>
            )}
            {isEditing && (
                <div>
                    <label htmlFor="password" className="block text-sm font-medium text-gray-700">Contraseña (dejar vacío para no cambiar)</label>
                    <input
                        type="password"
                        name="password"
                        id="password"
                        value={formData.password}
                        onChange={handleChange}
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                    />
                </div>
            )}

            <div>
                <label htmlFor="first_name" className="block text-sm font-medium text-gray-700">Nombre</label>
                <input
                    type="text"
                    name="first_name"
                    id="first_name"
                    value={formData.first_name}
                    onChange={handleChange}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                />
            </div>

            <div>
                <label htmlFor="last_name" className="block text-sm font-medium text-gray-700">Apellido</label>
                <input
                    type="text"
                    name="last_name"
                    id="last_name"
                    value={formData.last_name}
                    onChange={handleChange}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                />
            </div>

            <div>
                <label htmlFor="puesto" className="block text-sm font-medium text-gray-700">Puesto</label>
                <select
                    name="puesto"
                    id="puesto"
                    value={formData.puesto}
                    onChange={handleChange}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                >
                    <option value="">Selecciona un puesto</option>
                    {PUESTO_CHOICES.map(choice => (
                        <option key={choice} value={choice}>{choice}</option>
                    ))}
                </select>
            </div>

            {/* NUEVO: Selector de Departamento dinámico */}
            <div>
                <label htmlFor="departamento" className="block text-sm font-medium text-gray-700">Departamento</label>
                <select
                    name="departamento"
                    id="departamento"
                    value={formData.departamento}
                    onChange={handleChange}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                >
                    <option value="">-- Selecciona un departamento --</option>
                    {departamentos.map(dep => (
                        <option key={dep.id} value={dep.id}>{dep.name}</option>
                    ))}
                </select>
            </div>
            {/* -------------------------------------------------- */}

            {/* Selector de Región dinámico */}
            <div>
                <label htmlFor="region" className="block text-sm font-medium text-gray-700">Región</label>
                <select
                    name="region"
                    id="region"
                    value={formData.region}
                    onChange={handleChange}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                >
                    <option value="">-- Selecciona una región --</option>
                    {regions.map(region => (
                        <option key={region.id} value={region.id}>{region.name}</option>
                    ))}
                </select>
            </div>

            <div>
                <label htmlFor="assigned_role_id" className="block text-sm font-medium text-gray-700">Rol <span className="text-red-500">*</span></label>
                <select
                    name="assigned_role_id"
                    id="assigned_role_id"
                    value={formData.assigned_role_id}
                    onChange={handleChange}
                    required
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                >
                    <option value="">-- Selecciona un rol --</option>
                    {roles.map(role => (
                        <option key={role.id} value={role.id}>{role.name}</option>  
                    ))}
                </select>
            </div>

            <div className="flex items-center space-x-4">
                <div className="flex items-center">
                    <input
                        id="is_staff"
                        name="is_staff"
                        type="checkbox"
                        checked={formData.is_staff}
                        onChange={handleChange}
                        className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                    />
                    <label htmlFor="is_staff" className="ml-2 block text-sm text-gray-900">
                        Es miembro del personal (acceso a Django Admin)
                    </label>
                </div>
                <div className="flex items-center">
                    <input
                        id="is_superuser"
                        name="is_superuser"
                        type="checkbox"
                        checked={formData.is_superuser}
                        onChange={handleChange}
                        className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                    />
                    <label htmlFor="is_superuser" className="ml-2 block text-sm text-gray-900">
                        Es Superusuario (todos los permisos)
                    </label>
                </div>
            </div>

            {isEditing && (
                <div>
                    <label htmlFor="status" className="block text-sm font-medium text-gray-700">Estado</label>
                    <select
                        name="status"
                        id="status"
                        value={formData.status}
                        onChange={handleChange}
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
                    >
                        {STATUS_CHOICES.map(choice => (
                            <option key={choice} value={choice}>{choice}</option>
                        ))}
                    </select>
                </div>
            )}

            <div className="flex justify-end space-x-3 mt-6">
                <button
                    type="button"
                    onClick={onClose}
                    className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                >
                    Cancelar
                </button>
                <button
                    type="submit"
                    disabled={loading}
                    className="px-4 py-2 bg-blue-600 text-white rounded-md text-sm font-medium hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
                >
                    {loading ? 'Guardando...' : (isEditing ? 'Actualizar Usuario' : 'Crear Usuario')}
                </button>
            </div>
        </form>
    );
}

export default UserForm;
