// C:\Proyectos\ITAM_System\itam_frontend\src\pages\masterdata\TiposActivosPage.js
import React, { useState, useEffect } from 'react';
import { getTiposActivos, deleteTipoActivo } from '../../api'; // Make sure to import the correct API functions
import TipoActivoFormModal from './TipoActivoFormModal'; // Make sure to import your modal component
import { toast } from 'react-toastify';
import { useAuth } from '../../context/AuthContext';

function TiposActivosPage() {
    const [tiposActivos, setTiposActivos] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentTipoActivo, setCurrentTipoActivo] = useState(null);
    const { hasPermission } = useAuth();

    // Define the permission variables
    const canAddTipoActivo = hasPermission('masterdata.add_tipoactivo');
    const canChangeTipoActivo = hasPermission('masterdata.change_tipoactivo');
    const canDeleteTipoActivo = hasPermission('masterdata.delete_tipoactivo');

    useEffect(() => {
        fetchTiposActivos();
    }, []);

    const fetchTiposActivos = async () => {
        try {
            const response = await getTiposActivos();
            setTiposActivos(response.data);
        } catch (error) {
            console.error('Error fetching tipos de activos:', error);
            toast.error('Error al cargar los tipos de activos.');
        }
    };

    const handleAddClick = () => {
        setCurrentTipoActivo(null);
        setIsModalOpen(true);
    };

    const handleEditClick = (tipoActivo) => {
        setCurrentTipoActivo(tipoActivo);
        setIsModalOpen(true);
    };

    // Define the handleDeleteClick function
    const handleDeleteClick = async (id) => {
        if (!window.confirm('¿Estás seguro de que quieres eliminar este tipo de activo?')) {
            return;
        }
        try {
            await deleteTipoActivo(id);
            toast.success('Tipo de activo eliminado correctamente.');
            fetchTiposActivos();
        } catch (error) {
            console.error('Error deleting tipo de activo:', error.response?.data || error.message);
            const errorMessage = error.response?.data?.detail || 'Error al eliminar el tipo de activo.';
            toast.error(errorMessage);
        }
    };

    const handleSaveSuccess = () => {
        fetchTiposActivos();
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        setCurrentTipoActivo(null);
    };

    return (
        <div className="p-6 bg-white shadow-md rounded-lg">
            <h1 className="text-3xl font-bold text-gray-800 mb-6">Gestión de Tipos de Activos</h1>

            {canAddTipoActivo && (
                <div className="mb-8">
                    <button
                        onClick={handleAddClick}
                        className="px-6 py-3 bg-green-600 text-white rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2"
                    >
                        Crear Nuevo Tipo de Activo
                    </button>
                </div>
            )}

            <h2 className="text-2xl font-semibold text-gray-700 mb-4">Listado de Tipos de Activos</h2>
            {tiposActivos.length === 0 ? (
                <p className="text-gray-600">No hay tipos de activos disponibles.</p>
            ) : (
                <div className="overflow-x-auto">
                    <table className="min-w-full bg-white border border-gray-200 rounded-lg">
                        <thead>
                            <tr className="bg-gray-100 border-b">
                                <th className="py-3 px-4 text-left text-sm font-semibold text-gray-600">ID</th>
                                <th className="py-3 px-4 text-left text-sm font-semibold text-gray-600">Nombre</th>
                                <th className="py-3 px-4 text-left text-sm font-semibold text-gray-600">Descripción</th>
                                {(canChangeTipoActivo || canDeleteTipoActivo) && <th className="py-3 px-4 text-left text-sm font-semibold text-gray-600">Acciones</th>}
                            </tr>
                        </thead>
                        <tbody>
                            {tiposActivos.map((tipoActivo) => (
                                <tr key={tipoActivo.id} className="border-b hover:bg-gray-50">
                                    <td className="py-3 px-4 text-gray-800">{tipoActivo.id}</td>
                                    <td className="py-3 px-4 text-gray-800">{tipoActivo.name}</td>
                                    <td className="py-3 px-4 text-gray-800">{tipoActivo.description || 'N/A'}</td>
                                    {(canChangeTipoActivo || canDeleteTipoActivo) && (
                                        <td className="py-3 px-4">
                                            {canChangeTipoActivo && (
                                                <button
                                                    onClick={() => handleEditClick(tipoActivo)}
                                                    className="px-3 py-1 bg-yellow-500 text-white rounded-md text-sm hover:bg-yellow-600 mr-2"
                                                >
                                                    Editar
                                                </button>
                                            )}
                                            {canDeleteTipoActivo && (
                                                <button
                                                    onClick={() => handleDeleteClick(tipoActivo.id)}
                                                    className="px-3 py-1 bg-red-500 text-white rounded-md text-sm hover:bg-red-600"
                                                >
                                                    Eliminar
                                                </button>
                                            )}
                                        </td>
                                    )}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
            
            <TipoActivoFormModal
                show={isModalOpen}
                onClose={handleCloseModal}
                onSaveSuccess={handleSaveSuccess}
                tipoActivoToEdit={currentTipoActivo}
            />
        </div>
    );
}

export default TiposActivosPage;
