// itam_frontend/src/pages/masterdata/ModelosComputoPage.js

import React, { useState, useEffect } from 'react';
import { getModelosComputo, deleteModeloComputo } from '../../api';
import ModeloComputoFormModal from './ModeloComputoFormModal'; // Lo crearemos en el paso 8
import { toast } from 'react-toastify';
import { useAuth } from '../../context/AuthContext';

function ModelosComputoPage() {
    const [modelos, setModelos] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentModelo, setCurrentModelo] = useState(null);
    const { hasPermission } = useAuth();

    const canAddModelos = hasPermission('masterdata.add_modelocomputo');
    const canChangeModelos = hasPermission('masterdata.change_modelocomputo');
    const canDeleteModelos = hasPermission('masterdata.delete_modelocomputo');

    useEffect(() => {
        fetchModelos();
    }, []);

    const fetchModelos = async () => {
        try {
            const response = await getModelosComputo();
            setModelos(response.data);
        } catch (error) {
            console.error('Error fetching modelos de cómputo:', error);
            toast.error('Error al cargar los modelos de cómputo.');
        }
    };

    const handleAddClick = () => {
        setCurrentModelo(null);
        setIsModalOpen(true);
    };

    const handleEditClick = (modelo) => {
        setCurrentModelo(modelo);
        setIsModalOpen(true);
    };

    const handleDeleteClick = async (id) => {
        if (!window.confirm('¿Estás seguro de que quieres eliminar este modelo de cómputo?')) {
            return;
        }
        try {
            await deleteModeloComputo(id);
            toast.success('Modelo de cómputo eliminado correctamente.');
            fetchModelos();
        } catch (error) {
            console.error('Error deleting modelo:', error.response?.data || error.message);
            const errorMessage = error.response?.data?.detail || 'Error al eliminar el modelo.';
            toast.error(errorMessage);
        }
    };

    const handleSaveSuccess = () => {
        fetchModelos();
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        setCurrentModelo(null);
    };

    return (
        <div className="p-6 bg-white shadow-md rounded-lg">
            <h1 className="text-3xl font-bold text-gray-800 mb-6">Gestión de Modelos de Cómputo</h1>
            {canAddModelos && (
                <div className="mb-8">
                    <button
                        onClick={handleAddClick}
                        className="px-6 py-3 bg-green-600 text-white rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2"
                    >
                        Crear Nuevo Modelo de Cómputo
                    </button>
                </div>
            )}
            <h2 className="text-2xl font-semibold text-gray-700 mb-4">Listado de Modelos de Cómputo</h2>
            {modelos.length === 0 ? (
                <p className="text-gray-600">No hay modelos de cómputo disponibles.</p>
            ) : (
                <div className="overflow-x-auto">
                    <table className="min-w-full bg-white border border-gray-200 rounded-lg">
                        <thead>
                            <tr className="bg-gray-100 border-b">
                                <th className="py-3 px-4 text-left text-sm font-semibold text-gray-600">Cód. Interno</th>
                                <th className="py-3 px-4 text-left text-sm font-semibold text-gray-600">Serie</th>
                                <th className="py-3 px-4 text-left text-sm font-semibold text-gray-600">Marca</th>
                                <th className="py-3 px-4 text-left text-sm font-semibold text-gray-600">Tipo Activo</th>
                                <th className="py-3 px-4 text-left text-sm font-semibold text-gray-600">CPU/RAM</th>
                                {(canChangeModelos || canDeleteModelos) && <th className="py-3 px-4 text-left text-sm font-semibold text-gray-600">Acciones</th>}
                            </tr>
                        </thead>
                        <tbody>
                            {modelos.map((modelo) => (
                                <tr key={modelo.id} className="border-b hover:bg-gray-50">
                                    <td className="py-3 px-4 text-gray-800">{modelo.codigo_interno}</td>
                                    <td className="py-3 px-4 text-gray-800">{modelo.serie}</td>
                                    <td className="py-3 px-4 text-gray-800">{modelo.marca_name}</td>
                                    <td className="py-3 px-4 text-gray-800">{modelo.tipo_activo_name}</td>
                                    <td className="py-3 px-4 text-gray-800">{modelo.procesador} / {modelo.ram}</td>
                                    {(canChangeModelos || canDeleteModelos) && (
                                        <td className="py-3 px-4">
                                            {canChangeModelos && (
                                                <button
                                                    onClick={() => handleEditClick(modelo)}
                                                    className="px-3 py-1 bg-yellow-500 text-white rounded-md text-sm hover:bg-yellow-600 mr-2"
                                                >
                                                    Editar
                                                </button>
                                            )}
                                            {canDeleteModelos && (
                                                <button
                                                    onClick={() => handleDeleteClick(modelo.id)}
                                                    className="px-3 py-1 bg-red-500 text-white rounded-md text-sm hover:bg-red-600"
                                                >
                                                    Eliminar
                                                </button>
                                            )}
                                        </td>
                                    )}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
            <ModeloComputoFormModal
                show={isModalOpen}
                onClose={handleCloseModal}
                onSaveSuccess={handleSaveSuccess}
                modeloToEdit={currentModelo}
            />
        </div>
    );
}

export default ModelosComputoPage;
