// src/components/Navbar.js
import React from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Navbar = () => {
    const { user, logout } = useAuth(); // Obtén el usuario y la función de logout
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    if (!user) { // Esto es una verificación de seguridad, pero App.js ya debería manejarlo
        return null;
    }

    return (
        <nav className="bg-white shadow-md p-4 flex justify-between items-center z-10">
            <div className="text-xl font-bold text-gray-800">
                Bienvenido, {user.first_name || user.username}
                {user.role_name && <span className="ml-2 text-sm text-gray-500">({user.role_name})</span>}
            </div>
            <div>
                <button
                    onClick={handleLogout}
                    className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded"
                >
                    Cerrar Sesión
                </button>
            </div>
        </nav>
    );
};

export default Navbar;
