// C:\Proyectos\ITAM_System\itam_frontend\src\components\UserCrud.js
import React, { useState, useEffect, useRef } from 'react'; // <-- ¡Importa useRef aquí!
import api from '../api';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

// Importa los componentes de Font Awesome
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faPlus, faEye, faEdit, faKey, faToggleOn, faToggleOff
} from '@fortawesome/free-solid-svg-icons';

function UserCrud() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showViewModal, setShowViewModal] = useState(false);
  const [showChangePasswordModal, setShowChangePasswordModal] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  const effectRan = useRef(false); // <-- ¡Añade este ref aquí!

  const fetchUsers = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await api.get('/users/');
      setUsers(response.data);
      toast.success('Usuarios cargados exitosamente.');
    } catch (err) {
      if (err.response && err.response.status === 401) {
        toast.error('Sesión expirada o no autorizada. Por favor, inicia sesión de nuevo.');
        localStorage.clear();
        navigate('/login');
      } else {
        setError('Error al cargar usuarios: ' + (err.response?.data?.detail || err.message));
        console.error("Error fetching users:", err);
        toast.error('Error al cargar usuarios.');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // === ¡Aplica esta lógica para controlar la doble ejecución en StrictMode! ===
    if (effectRan.current === false) {
      fetchUsers();
      effectRan.current = true;
    }
    // =========================================================================
  }, []); // Dependencias vacías

  // ... el resto de tu código del componente UserCrud ...

  return (
    <div className="container mx-auto p-4">
      {/* ... tu JSX existente ... */}
    </div>
  );
}

export default UserCrud;
