// src/components/Home.js
import React, { useState, useEffect } from 'react';
import Modal from './Modal';
import { getDashboardData, getDashboardModelsData, getDashboardWarrantyData, getDashboardSummary, getDashboardDetailData } from '../api';

function Home() {
  const [dashboardData, setDashboardData] = useState(null);
  const [modelsData, setModelsData] = useState(null);
  const [warrantyData, setWarrantyData] = useState(null);
  const [summaryData, setSummaryData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Modal state
  const [showModal, setShowModal] = useState(false);
  const [modalData, setModalData] = useState(null);
  const [modalTitle, setModalTitle] = useState('');
  const [modalLoading, setModalLoading] = useState(false);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        // Fetch data individually to better handle errors
        const dashboardResponse = await getDashboardData();
        setDashboardData(dashboardResponse.data);

        const modelsResponse = await getDashboardModelsData();
        setModelsData(modelsResponse.data);

        const warrantyResponse = await getDashboardWarrantyData();
        setWarrantyData(warrantyResponse.data);

        // Try to fetch summary data, but don't fail if it doesn't work
        try {
          const summaryResponse = await getDashboardSummary();
          setSummaryData(summaryResponse.data);
        } catch (summaryErr) {
          console.warn('Could not load summary data:', summaryErr);
          // Set empty summary data so the UI still works
          setSummaryData({
            total_assets: 0,
            computer_assets: 0,
            valid_warranty: 0,
            expiring_warranty: 0,
            no_or_expired_warranty: 0
          });
        }

      } catch (err) {
        setError('Error al cargar los datos del dashboard');
        console.error('Dashboard error:', err);
        // Set default empty data so the UI doesn't break completely
        setDashboardData(null);
        setModelsData(null);
        setWarrantyData(null);
        setSummaryData(null);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  const handleCardClick = async (category, title) => {
    setModalLoading(true);
    setShowModal(true);
    setModalTitle(title);

    try {
      const response = await getDashboardDetailData(category);
      setModalData(response.data);
    } catch (err) {
      console.error('Error loading modal data:', err);
      setModalData(null);
    } finally {
      setModalLoading(false);
    }
  };

  const closeModal = () => {
    setShowModal(false);
    setModalData(null);
    setModalTitle('');
  };

  if (loading) {
    return (
      <div className="min-h-full flex items-center justify-center">
        <div className="text-lg">Cargando dashboard...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-full flex items-center justify-center">
        <div className="text-red-600">{error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-full p-6 bg-gray-50">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Dashboard ITAM</h1>

      {/* Summary Cards */}
      {summaryData && !loading && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4 mb-6">
          <div className="bg-white rounded-lg shadow-md p-4 border-l-4 border-blue-500">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <div className="w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center">
                  <span className="text-white text-sm font-bold">‚àë</span>
                </div>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Activos</p>
                <p className="text-2xl font-bold text-gray-900">{summaryData.total_assets || 0}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-4 border-l-4 border-purple-500">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <div className="w-8 h-8 bg-purple-500 rounded-full flex items-center justify-center">
                  <span className="text-white text-sm font-bold">üíª</span>
                </div>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Equipos de C√≥mputo</p>
                <p className="text-2xl font-bold text-gray-900">{summaryData.computer_assets || 0}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-4 border-l-4 border-green-500">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <div className="w-8 h-8 bg-green-500 rounded-full flex items-center justify-center">
                  <span className="text-white text-sm font-bold">‚úì</span>
                </div>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Garant√≠a Vigente</p>
                <p className="text-2xl font-bold text-gray-900">{summaryData.valid_warranty || 0}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-4 border-l-4 border-yellow-500">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <div className="w-8 h-8 bg-yellow-500 rounded-full flex items-center justify-center">
                  <span className="text-white text-sm font-bold">‚è∞</span>
                </div>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Garant√≠a Pr√≥xima</p>
                <p className="text-2xl font-bold text-gray-900">{summaryData.expiring_warranty || 0}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-4 border-l-4 border-red-500">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <div className="w-8 h-8 bg-red-500 rounded-full flex items-center justify-center">
                  <span className="text-white text-sm font-bold">‚ö†</span>
                </div>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Sin Garant√≠a</p>
                <p className="text-2xl font-bold text-gray-900">{summaryData.no_or_expired_warranty || 0}</p>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="flex gap-6">
        {/* Secci√≥n 1: Tabla de tipos de activos por regi√≥n */}
        <div className="bg-white rounded-lg shadow-md p-6 flex-1">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">Distribuci√≥n de Activos por Tipo y Regi√≥n</h2>
          {dashboardData && dashboardData.tipos_activo.length > 0 ? (
            <div className="overflow-x-auto max-h-64 overflow-y-auto">
              <table className="min-w-full table-auto border-collapse border border-gray-300 text-sm">
                <thead className="sticky top-0 bg-gray-100">
                  <tr>
                    <th className="border border-gray-300 px-2 py-1 text-left">Tipo de Activo</th>
                    {dashboardData.regions.map(region => (
                      <th key={region} className="border border-gray-300 px-2 py-1 text-center">{region}</th>
                    ))}
                    <th className="border border-gray-300 px-2 py-1 text-center">Total</th>
                  </tr>
                </thead>
                <tbody>
                  {dashboardData.tipos_activo.map((tipo, index) => (
                    <tr key={tipo.name} className={index === dashboardData.tipos_activo.length - 1 ? 'bg-gray-200 font-bold' : 'hover:bg-gray-50'}>
                      <td className="border border-gray-300 px-2 py-1">{tipo.name}</td>
                      {dashboardData.regions.map(region => (
                        <td key={region} className="border border-gray-300 px-2 py-1 text-center">{tipo.counts[region] || 0}</td>
                      ))}
                      <td className="border border-gray-300 px-2 py-1 text-center">{tipo.total}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-gray-500">No hay datos disponibles</p>
          )}
        </div>

        {/* Secci√≥n 2: Tabla de modelos de activos por regi√≥n */}
        <div className="bg-white rounded-lg shadow-md p-6 flex-1">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">Distribuci√≥n de Equipos por Modelo y Regi√≥n</h2>
          {modelsData && modelsData.modelos_activo.length > 0 ? (
            <div className="overflow-x-auto max-h-64 overflow-y-auto">
              <table className="min-w-full table-auto border-collapse border border-gray-300 text-sm">
                <thead className="sticky top-0 bg-gray-100">
                  <tr>
                    <th className="border border-gray-300 px-2 py-1 text-left">Modelo de Activo</th>
                    {modelsData.regions.map(region => (
                      <th key={region} className="border border-gray-300 px-2 py-1 text-center">{region}</th>
                    ))}
                    <th className="border border-gray-300 px-2 py-1 text-center">Total</th>
                  </tr>
                </thead>
                <tbody>
                  {modelsData.modelos_activo.map((modelo, index) => (
                    <tr key={modelo.name} className={index === modelsData.modelos_activo.length - 1 ? 'bg-gray-200 font-bold' : 'hover:bg-gray-50'}>
                      <td className="border border-gray-300 px-2 py-1">{modelo.name}</td>
                      {modelsData.regions.map(region => (
                        <td key={region} className="border border-gray-300 px-2 py-1 text-center">{modelo.counts[region] || 0}</td>
                      ))}
                      <td className="border border-gray-300 px-2 py-1 text-center">{modelo.total}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-gray-500">No hay datos disponibles</p>
          )}
        </div>

        {/* Secci√≥n 3: Tabla de garant√≠as pr√≥ximas a vencer */}
        <div className="bg-white rounded-lg shadow-md p-6 flex-1">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">Garant√≠as Pr√≥ximas a Vencer (90 d√≠as)</h2>
          {warrantyData && warrantyData.warranty_info.length > 0 ? (
            <div className="overflow-x-auto max-h-64 overflow-y-auto">
              <table className="min-w-full table-auto border-collapse border border-gray-300 text-sm">
                <thead className="sticky top-0 bg-gray-100">
                  <tr>
                    <th className="border border-gray-300 px-2 py-1 text-left">Fecha de Vencimiento</th>
                    <th className="border border-gray-300 px-2 py-1 text-left">Regi√≥n</th>
                    <th className="border border-gray-300 px-2 py-1 text-left">Modelo</th>
                    <th className="border border-gray-300 px-2 py-1 text-center">Cantidad</th>
                  </tr>
                </thead>
                <tbody>
                  {warrantyData.warranty_info.map((item, index) => (
                    <tr key={`${item.expiry_date}-${item.region}-${item.model}`} className="hover:bg-gray-50">
                      <td className="border border-gray-300 px-2 py-1">
                        {new Date(item.expiry_date).toLocaleDateString('es-GT')}
                      </td>
                      <td className="border border-gray-300 px-2 py-1">{item.region}</td>
                      <td className="border border-gray-300 px-2 py-1">{item.model}</td>
                      <td className="border border-gray-300 px-2 py-1 text-center">{item.count}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-gray-500">No hay garant√≠as pr√≥ximas a vencer</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Home;
