// src/components/Login.js
import React, { useState } from 'react';
import axios from 'axios'; // Importa Axios para las peticiones HTTP
import { useNavigate } from 'react-router-dom'; // Para redireccionar después del login

function Login({ onLoginSuccess }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // Hook para la navegación

  const handleSubmit = async (e) => {
    e.preventDefault(); // Previene el comportamiento por defecto del formulario (recargar la página)
    setError(''); // Limpia cualquier error anterior

    try {
      // Realiza la petición POST al endpoint de login de tu backend Django
      const response = await axios.post('http://127.0.0.1:8000/api/login/', {
        username,
        password,
      });

      // Si el login es exitoso
      const { access, refresh, user_id, username: loggedInUsername, email } = response.data;

      // Almacena los tokens y la información del usuario en el almacenamiento local del navegador
      localStorage.setItem('accessToken', access);
      localStorage.setItem('refreshToken', refresh);
      localStorage.setItem('userId', user_id);
      localStorage.setItem('username', loggedInUsername);
      localStorage.setItem('email', email);

      // Llama a la función de éxito que se pasa desde App.js
      onLoginSuccess();

      // Redirecciona a la página principal después del login
      navigate('/');

    } catch (err) {
      // Manejo de errores
      if (err.response) {
        // El servidor respondió con un estado de error (ej. 401 Unauthorized)
        setError(err.response.data.error || 'Credenciales inválidas. Inténtalo de nuevo.');
      } else if (err.request) {
        // La petición fue hecha pero no se recibió respuesta (ej. servidor caído, sin internet)
        setError('No se pudo conectar con el servidor. Verifica tu conexión.');
      } else {
        // Algo más causó el error
        setError('Ocurrió un error inesperado. Inténtalo de nuevo.');
      }
      console.error("Error de login:", err);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-sm">
        <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">Iniciar Sesión</h2>
        <form onSubmit={handleSubmit}>
          {error && ( // Muestra el error si existe
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">
              <span className="block sm:inline">{error}</span>
            </div>
          )}
          <div className="mb-4">
            <label htmlFor="username" className="block text-gray-700 text-sm font-bold mb-2">
              Nombre de Usuario:
            </label>
            <input
              type="text"
              id="username"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="mb-6">
            <label htmlFor="password" className="block text-gray-700 text-sm font-bold mb-2">
              Contraseña:
            </label>
            <input
              type="password"
              id="password"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="flex items-center justify-between">
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full"
            >
              Entrar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Login;
