// itam_frontend/src/pages/masterdata/MarcasPage.js

import React, { useState, useEffect } from 'react';
import { getMarcas, deleteMarca } from '../../api';
import MarcaFormModal from './MarcaFormModal';
import { toast } from 'react-toastify';
import { useAuth } from '../../context/AuthContext';

function MarcasPage() {
    const [marcas, setMarcas] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentMarca, setCurrentMarca] = useState(null);
    const { hasPermission } = useAuth();

    const canAddMarca = hasPermission('masterdata.add_marca');
    const canChangeMarca = hasPermission('masterdata.change_marca');
    const canDeleteMarca = hasPermission('masterdata.delete_marca');

    useEffect(() => {
        fetchMarcas();
    }, []);

    const fetchMarcas = async () => {
        try {
            const response = await getMarcas();
            setMarcas(response.data);
        } catch (error) {
            console.error('Error fetching marcas:', error);
            toast.error('Error al cargar las marcas.');
        }
    };

    const handleAddClick = () => {
        setCurrentMarca(null);
        setIsModalOpen(true);
    };

    const handleEditClick = (marca) => {
        setCurrentMarca(marca);
        setIsModalOpen(true);
    };

    const handleDeleteClick = async (id) => {
        if (!window.confirm('¿Estás seguro de que quieres eliminar esta marca?')) {
            return;
        }
        try {
            await deleteMarca(id);
            toast.success('Marca eliminada correctamente.');
            fetchMarcas();
        } catch (error) {
            console.error('Error deleting marca:', error.response?.data || error.message);
            const errorMessage = error.response?.data?.detail || 'Error al eliminar la marca.';
            toast.error(errorMessage);
        }
    };

    const handleSaveSuccess = () => {
        fetchMarcas();
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        setCurrentMarca(null);
    };

    return (
        <div className="p-6 bg-white shadow-md rounded-lg">
            <h1 className="text-3xl font-bold text-gray-800 mb-6">Gestión de Marcas</h1>
            {canAddMarca && (
                <div className="mb-8">
                    <button
                        onClick={handleAddClick}
                        className="px-6 py-3 bg-green-600 text-white rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2"
                    >
                        Crear Nueva Marca
                    </button>
                </div>
            )}
            <h2 className="text-2xl font-semibold text-gray-700 mb-4">Listado de Marcas</h2>
            {marcas.length === 0 ? (
                <p className="text-gray-600">No hay marcas disponibles.</p>
            ) : (
                <div className="overflow-x-auto">
                    <table className="min-w-full bg-white border border-gray-200 rounded-lg">
                        <thead>
                            <tr className="bg-gray-100 border-b">
                                <th className="py-3 px-4 text-left text-sm font-semibold text-gray-600">ID</th>
                                <th className="py-3 px-4 text-left text-sm font-semibold text-gray-600">Nombre</th>
                                <th className="py-3 px-4 text-left text-sm font-semibold text-gray-600">Descripción</th>
                                {(canChangeMarca || canDeleteMarca) && <th className="py-3 px-4 text-left text-sm font-semibold text-gray-600">Acciones</th>}
                            </tr>
                        </thead>
                        <tbody>
                            {marcas.map((marca) => (
                                <tr key={marca.id} className="border-b hover:bg-gray-50">
                                    <td className="py-3 px-4 text-gray-800">{marca.id}</td>
                                    <td className="py-3 px-4 text-gray-800">{marca.name}</td>
                                    <td className="py-3 px-4 text-gray-800">{marca.description || 'N/A'}</td>
                                    {(canChangeMarca || canDeleteMarca) && (
                                        <td className="py-3 px-4">
                                            {canChangeMarca && (
                                                <button
                                                    onClick={() => handleEditClick(marca)}
                                                    className="px-3 py-1 bg-yellow-500 text-white rounded-md text-sm hover:bg-yellow-600 mr-2"
                                                >
                                                    Editar
                                                </button>
                                            )}
                                            {canDeleteMarca && (
                                                <button
                                                    onClick={() => handleDeleteClick(marca.id)}
                                                    className="px-3 py-1 bg-red-500 text-white rounded-md text-sm hover:bg-red-600"
                                                >
                                                    Eliminar
                                                </button>
                                            )}
                                        </td>
                                    )}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
            <MarcaFormModal
                show={isModalOpen}
                onClose={handleCloseModal}
                onSaveSuccess={handleSaveSuccess}
                marcaToEdit={currentMarca}
            />
        </div>
    );
}

export default MarcasPage;
