import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';
import { toast } from 'react-toastify';
import TipoActivoFormModal from './TipoActivoFormModal';
import { getTiposActivos, deleteTipoActivo } from '../../api';
import { useAuth } from '../../context/AuthContext';

function TiposActivosPage() {
    const { hasPermission } = useAuth();
    const [tiposActivos, setTiposActivos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showModal, setShowModal] = useState(false);
    const [tipoActivoToEdit, setTipoActivoToEdit] = useState(null);

    const canCreate = hasPermission('masterdata.add_tipoactivo');
    const canEdit = hasPermission('masterdata.change_tipoactivo');
    const canDelete = hasPermission('masterdata.delete_tipoactivo');

    useEffect(() => {
        fetchTiposActivos();
    }, []);

    const fetchTiposActivos = async () => {
        setLoading(true);
        try {
            const response = await getTiposActivos();
            setTiposActivos(response.data);
            setLoading(false);
        } catch (error) {
            toast.error("Error al cargar los tipos de activos.");
            console.error("Error fetching tipos de activos:", error);
            setLoading(false);
        }
    };

    const handleDelete = async (id) => {
        if (!window.confirm("¿Está seguro de que desea eliminar este tipo de activo?")) return;

        try {
            await deleteTipoActivo(id);
            toast.success("Tipo de activo eliminado exitosamente!");
            fetchTiposActivos();
        } catch (error) {
            console.error("Error deleting tipo de activo:", error.response?.data || error.message);
            const errorMessage = error.response?.data?.detail || 'Error al eliminar el tipo de activo. Puede que esté en uso.';
            toast.error(errorMessage);
        }
    };

    const handleEditClick = (tipoActivo) => {
        setTipoActivoToEdit(tipoActivo);
        setShowModal(true);
    };

    const handleCreateClick = () => {
        setTipoActivoToEdit(null);
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
        setTipoActivoToEdit(null);
    };

    if (loading) {
        return <div className="text-center py-8">Cargando tipos de activos...</div>;
    }

    return (
        <div className="container mx-auto p-4">
            <div className="flex justify-between items-center mb-6">
                <h1 className="text-3xl font-bold text-gray-800">Gestión de Tipos de Activos</h1>
                {canCreate && (
                    <button
                        onClick={handleCreateClick}
                        className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg flex items-center shadow-lg transition duration-300"
                    >
                        <FontAwesomeIcon icon={faPlus} className="mr-2" />
                        Añadir Tipo de Activo
                    </button>
                )}
            </div>

            <div className="bg-white shadow-lg rounded-lg overflow-hidden">
                <div className="p-4 overflow-x-auto">
                    <table className="min-w-full leading-normal">
                        <thead>
                            <tr>
                                <th className="px-5 py-3 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                                    ID
                                </th>
                                <th className="px-5 py-3 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                                    Nombre
                                </th>
                                <th className="px-5 py-3 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                                    Descripción
                                </th>
                                <th className="px-5 py-3 border-b-2 border-gray-200 bg-gray-100 text-center text-xs font-semibold text-gray-600 uppercase tracking-wider">
                                    Acciones
                                </th>
                            </tr>
                        </thead>
                        <tbody>
                            {tiposActivos.length > 0 ? (
                                tiposActivos.map((tipo) => (
                                    <tr key={tipo.id} className="hover:bg-gray-50">
                                        <td className="px-5 py-5 border-b border-gray-200 bg-white text-sm">
                                            {tipo.id}
                                        </td>
                                        <td className="px-5 py-5 border-b border-gray-200 bg-white text-sm">
                                            {tipo.name}
                                        </td>
                                        <td className="px-5 py-5 border-b border-gray-200 bg-white text-sm">
                                            {tipo.description}
                                        </td>
                                        <td className="py-3 px-4">
                                            {canChangeTipoActivo && (
                                                <button
                                                    onClick={() => handleEditClick(tipoActivo)}
                                                    className="px-3 py-1 bg-yellow-500 text-white rounded-md text-sm hover:bg-yellow-600 mr-2"
                                                >
                                                    Editar
                                                </button>
                                            )}
                                            {canDeleteTipoActivo && (
                                                <button
                                                    onClick={() => handleDeleteClick(tipoActivo.id)}
                                                    className="px-3 py-1 bg-red-500 text-white rounded-md text-sm hover:bg-red-600"
                                                >
                                                    Eliminar
                                                </button>
                                            )}
</td>
                                    </tr>
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="4" className="text-center py-4 text-gray-500">
                                        No se encontraron tipos de activos.
                                    </td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </div>

            <TipoActivoFormModal
                show={showModal}
                onClose={handleCloseModal}
                onSaveSuccess={fetchTiposActivos}
                tipoActivoToEdit={tipoActivoToEdit}
            />
        </div>
    );
}

export default TiposActivosPage;
