// src/components/UserForm.js
import React, { useState, useEffect } from 'react';
import api from '../api';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

// Opciones para los campos de selección (mantener estos si son estáticos)
const PUESTO_CHOICES = [
  'Gerente', 'Coordinador', 'Analista', 'Técnico', 'Desarrollador', 'Soporte', 'Otro'
];
const DEPARTAMENTO_CHOICES = [
  'TI', 'Recursos Humanos', 'Finanzas', 'Marketing', 'Ventas', 'Operaciones', 'Otro'
];
const REGION_CHOICES = [
  'Norte', 'Centro', 'Sur', 'Este', 'Oeste', 'Nacional', 'Internacional', 'Otro'
];
const STATUS_CHOICES = [
  'Activo', 'Inactivo', 'Vacaciones', 'Licencia'
];

function UserForm({ user, onClose }) {
  const isEditing = !!user;

  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    first_name: '',
    last_name: '',
    puesto: '',
    departamento: '',
    region: '',
    status: 'Activo',
    // !!! CAMBIADO: Ahora almacenaremos el ID del rol seleccionado
    // Si un usuario tiene múltiples roles, deberías usar un array aquí: rolIds: []
    // Por simplicidad, asumimos que solo se asignará un rol principal.
    assigned_role_id: '', // Almacenará el ID del grupo seleccionado
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [roles, setRoles] = useState([]); // Almacenará los objetos de rol {id, name}
  const navigate = useNavigate();

  // Precargar datos si estamos editando
  useEffect(() => {
    if (isEditing && user) {
      // Si el usuario tiene un rol asignado (role_id), precárgalo
      setFormData({
        username: user.username || '',
        email: user.email || '',
        password: '',
        first_name: user.first_name || '',
        last_name: user.last_name || '',
        puesto: user.puesto || '',
        departamento: user.departamento || '',
        region: user.region || '',
        status: user.status || 'Activo',
        // !!! CAMBIADO: Precargamos el ID del rol si existe
        assigned_role_id: user.role_id || '', // role_id es el campo de solo lectura del serializador
      });
    } else {
      // Para nuevos usuarios, si quieres un rol por defecto, puedes establecerlo aquí
      // const defaultRole = roles.find(r => r.name === "Empleado");
      // if (defaultRole) setFormData(prev => ({...prev, assigned_role_id: defaultRole.id}));
    }
  }, [isEditing, user, roles]); // Agregamos 'roles' como dependencia para el valor por defecto

  // !!! MODIFICADO: useEffect para cargar los roles (grupos) desde el backend
  useEffect(() => {
    const fetchRoles = async () => {
      try {
        // Llama al endpoint de grupos/roles que devuelve {id, name, permissions}
        const response = await api.get('/roles/'); // <-- Esta URL es para RoleListCreateAPIView
        setRoles(response.data); // La respuesta es un array de objetos de rol
      } catch (err) {
        console.error("Error al cargar los roles:", err.response || err);
        toast.error('Error al cargar la lista de roles.');
      }
    };
    fetchRoles();
  }, []); // Se ejecuta solo una vez al montar

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const dataToSend = { ...formData };

      // Si la contraseña está vacía y estamos editando, no la enviamos.
      // Si estamos creando y la contraseña está vacía, mostramos error (ya manejado antes).
      if (isEditing && !dataToSend.password) {
        delete dataToSend.password;
      }
      if (!isEditing && !dataToSend.password) {
        setError('La contraseña es obligatoria para nuevos usuarios.');
        toast.error('La contraseña es obligatoria para nuevos usuarios.');
        setLoading(false);
        return;
      }

      // !!! CRUCIAL: Mapea el assigned_role_id a un array de IDs para el backend
      // El backend espera `assigned_role_id: [ID_DEL_GRUPO]`.
      // Si no se seleccionó ningún rol, envía un array vacío o null.
      if (dataToSend.assigned_role_id) {
        dataToSend.assigned_role_id = [parseInt(dataToSend.assigned_role_id, 10)];
      } else {
        dataToSend.assigned_role_id = []; // O null si tu backend lo permite y quieres quitar el rol
      }

      if (isEditing) {
        await api.put(`/users/${user.id}/`, dataToSend);
        toast.success('Usuario actualizado exitosamente!');
      } else {
        await api.post('/users/', dataToSend); // Enviamos dataToSend que ahora incluye assigned_role_id
        toast.success('Usuario creado exitosamente!');
      }
      onClose();
    } catch (err) {
      if (err.response && err.response.status === 401) {
        toast.error('Sesión expirada o no autorizada. Por favor, inicia sesión de nuevo.');
        localStorage.clear();
        navigate('/login');
      } else {
        const errorMsg = err.response?.data
          ? Object.values(err.response.data).flat().join(' ')
          : err.message;
        setError('Error: ' + errorMsg);
        console.error("Error submitting user form:", err.response || err);
        toast.error('Error al guardar usuario: ' + errorMsg);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
          <span className="block sm:inline">{error}</span>
        </div>
      )}

      <div>
        <label htmlFor="username" className="block text-sm font-medium text-gray-700">Usuario <span className="text-red-500">*</span></label>
        <input
          type="text"
          name="username"
          id="username"
          value={formData.username}
          onChange={handleChange}
          required
          className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
          disabled={isEditing}
        />
      </div>

      <div>
        <label htmlFor="email" className="block text-sm font-medium text-gray-700">Correo <span className="text-red-500">*</span></label>
        <input
          type="email"
          name="email"
          id="email"
          value={formData.email}
          onChange={handleChange}
          required
          className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
        />
      </div>

      {!isEditing && (
        <div>
          <label htmlFor="password" className="block text-sm font-medium text-gray-700">Contraseña <span className="text-red-500">*</span></label>
          <input
            type="password"
            name="password"
            id="password"
            value={formData.password}
            onChange={handleChange}
            required={!isEditing}
            className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
          />
        </div>
      )}
      {isEditing && (
          <div>
            <label htmlFor="password" className="block text-sm font-medium text-gray-700">Contraseña (dejar vacío para no cambiar)</label>
            <input
              type="password"
              name="password"
              id="password"
              value={formData.password}
              onChange={handleChange}
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
            />
          </div>
      )}

      <div>
        <label htmlFor="first_name" className="block text-sm font-medium text-gray-700">Nombre</label>
        <input
          type="text"
          name="first_name"
          id="first_name"
          value={formData.first_name}
          onChange={handleChange}
          className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
        />
      </div>

      <div>
        <label htmlFor="last_name" className="block text-sm font-medium text-gray-700">Apellido</label>
        <input
          type="text"
          name="last_name"
          id="last_name"
          value={formData.last_name}
          onChange={handleChange}
          className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
        />
      </div>

      <div>
        <label htmlFor="puesto" className="block text-sm font-medium text-gray-700">Puesto</label>
        <select
          name="puesto"
          id="puesto"
          value={formData.puesto}
          onChange={handleChange}
          className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
        >
          <option value="">Selecciona un puesto</option>
          {PUESTO_CHOICES.map(choice => (
            <option key={choice} value={choice}>{choice}</option>
          ))}
        </select>
      </div>

      <div>
        <label htmlFor="departamento" className="block text-sm font-medium text-gray-700">Departamento</label>
        <select
          name="departamento"
          id="departamento"
          value={formData.departamento}
          onChange={handleChange}
          className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
        >
          <option value="">Selecciona un departamento</option>
          {DEPARTAMENTO_CHOICES.map(choice => (
            <option key={choice} value={choice}>{choice}</option>
          ))}
        </select>
      </div>

      <div>
        <label htmlFor="region" className="block text-sm font-medium text-gray-700">Región</label>
        <select
          name="region"
          id="region"
          value={formData.region}
          onChange={handleChange}
          className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
        >
          <option value="">Selecciona una región</option>
          {REGION_CHOICES.map(choice => (
            <option key={choice} value={choice}>{choice}</option>
          ))}
        </select>
      </div>

      {/* !!! CAMBIADO: Campo de selección para el Rol, ahora dinámico y enviando ID */}
      <div>
        <label htmlFor="assigned_role_id" className="block text-sm font-medium text-gray-700">Rol <span className="text-red-500">*</span></label>
        <select
          name="assigned_role_id" // !!! Cambiado de 'rol' a 'assigned_role_id'
          id="assigned_role_id"
          value={formData.assigned_role_id}
          onChange={handleChange}
          required
          className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
        >
          <option value="">Selecciona un rol</option>
          {roles.map(role => (
            <option key={role.id} value={role.id}>{role.name}</option> 
          ))}
        </select>
      </div>

      {isEditing && (
        <div>
          <label htmlFor="status" className="block text-sm font-medium text-gray-700">Estado</label>
          <select
            name="status"
            id="status"
            value={formData.status}
            onChange={handleChange}
            className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2 focus:ring-blue-500 focus:border-blue-500"
          >
            {STATUS_CHOICES.map(choice => (
              <option key={choice} value={choice}>{choice}</option>
            ))}
          </select>
        </div>
      )}

      <div className="flex justify-end space-x-3 mt-6">
        <button
          type="button"
          onClick={onClose}
          className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
        >
          Cancelar
        </button>
        <button
          type="submit"
          disabled={loading}
          className="px-4 py-2 bg-blue-600 text-white rounded-md text-sm font-medium hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
        >
          {loading ? 'Guardando...' : (isEditing ? 'Actualizar Usuario' : 'Crear Usuario')}
        </button>
      </div>
    </form>
  );
}

export default UserForm;
